    useEffect(() => {
      fetch(`https://pokeapi.co/api/v2/pokemon/${props.pokemonId}/`)
        .then((res) => res.json())
        .then((data) => {
          setPokemonData(data);
          // console.log(data);
        });
    }, [props]);
return (
    <div>
      {pokemonData ? (
        <>
            <p>{pokemonData.name}'s moves:</p>
